import React, { useEffect, useState } from "react";
import NavBar from "./components/NavBar";
import { Container } from "react-bootstrap";
import CardMovies from "./components/CardMovies";
import axios from "axios";
import MovieList from "./components/MovieList";
import Text from "./components/text";
function App() {
  const [movies, setMovies] = useState([]);
  useS

  //get all movies axios
  const getAllMovies = async () => {
    const res = await axios.get(
      "https://api.themoviedb.org/3/discover/movie?api_key=98203601b54b80cfc8d22a0b91d25140"
    );
    setMovies(res.data.results);
  };

  // تنفيذ الداله عند فتح الموقع
  useEffect(() => {
    getAllMovies();
  }, []);

  // search in API
  const searchMovies = async (word) => {
    if ((word === "")) {
      getAllMovies();
    } else {
      const res = await axios.get(
        `https://api.themoviedb.org/3/search/movie?api_key=98203601b54b80cfc8d22a0b91d25140&query=${word}`
      );
      setMovies(res.data.results);
    }
  };

  // Get Current Page
  const getPage = async (page) => {
    const res = await axios.get(
      `https://api.themoviedb.org/3/search/movie?api_key=98203601b54b80cfc8d22a0b91d25140&page=${page}`
    );
    setMovies(res.data.results);
  };

  return (
    <div className="">
      <NavBar searchMovies={searchMovies} />
      <Container>
        <Text/>
        <MovieList movies={movies} getPage={getPage}/>
      </Container>
    </div>
  );
}

export default App;
